import React, { setState,useState } from "react";
import ReactPlayer from "react-player";

const UploadForm = () => {
  const {label, description, floor,section} = useState(null);
  const [file, setFile] = useState(null);
  const [error, setError] = useState(null);
  const [videoFilePath, setVideoFilePath] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const types = ["video/avi", "video/mp4", "image/png"];

  const changeHandler = (e) => {
    this.setState({
        [e.target.name]: e.target.value
    })

    let selected = e.target.files[0];
    console.log(selected);
    setShowForm(!showForm);
    if (selected && types.includes(selected.type)) {
      setVideoFilePath(URL.createObjectURL(e.target.files[0]));
      setFile(selected);
      setError("");
    } else {
      setFile(null);
      setError("Please select a video file (.mp4 or .avi)");
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("file", file, file.name);

    const requestOptions = {
      method: "POST",
      body: formData,
    };

    fetch("http://127.0.0.1:8000/upload", requestOptions)
      .then((response) => response.json())
      .then(function (response) {
        console.log(response);
      });
  };

  return (
    <form>
      <input type="file" onChange={changeHandler} />
      <div className="output">
        {error && <div className="error">{error}</div>}
        {file && <div>{file.name}</div>}
      </div>

      {showForm && (
        <div className="rowC">
          <div>
            <ReactPlayer url={videoFilePath} controls={true} />
          </div>
          <div>
            <div>
              <label>label</label>
              <input type="text" name ='label' 
                onChange={this.changeHandler}
                value={label} />
            </div>
            <div>
              <label>Description</label>
              <input type="text" name ='description' 
                onChange={this.changeHandler}
                value={description} />
            </div>
            <div>
              <label>Floor</label>
              <input type="text" name ='floor' 
                onChange={this.changeHandler}
                value={floor} />
            </div>
            <div>
              <label>Section</label>
              <input type="text" name ='section' 
                onChange={this.changeHandler}
                value={section} />
            </div>
            <div>
              <button onClick={handleSubmit}> Submit </button>
            </div>
          </div>
        </div>
      )}
    </form>
  );
};

export default UploadForm;
