import React, {useState} from 'react';
import ReactPlayer from 'react-player';

const UploadForm = () => {
    const [file, setFile] = useState(null);
    const [error, setError] = useState(null);
    const [videoFilePath, setVideoFilePath] = useState(null);

    const types = ['video/avi', 'video/mp4', 'image/png'];

    const changeHandler = (e) => {
        let selected = e.target.files[0];
        console.log(selected);

        if (selected && types.includes(selected.type)) {
            setVideoFilePath(URL.createObjectURL(e.target.files[0]));
            setFile(selected);
            setError('')
        } else {
            setFile(null);
            setError('Please select a video file (.mp4 or .avi)')
        }
    }

    const handleSubmit = (e) => {
        const [setShowForm] = useState(false);

  const showForm = () => {
    setShowForm(!showForm);
  }

  return (
    <div>
      <form>
        <button onClick={showForm}></button>
      </form>

      {showForm && (
        <form>
          ...
        </form>
      )}
    </div>
  )
        const formData = new FormData();
        formData.append(
            "file",
            file,
            file.name
        );

        const requestOptions = {
            method : 'POST',
            body : formData
        };

        fetch("http://127.0.0.1:8000/upload", requestOptions)
        .then(response => response.json())
        .then(function(response){
            console.log(response)
        })
    }


    return (
        <form>
            <input type="file" onChange={changeHandler}/>
            <div className='output'>
                {error && <div className="error">{error}</div>}
                {file && <div>{file.name}</div>}
            </div> 
            <button onClick={handleSubmit}> Submit </button>
            <ReactPlayer url={videoFilePath} width="50%" height="50%" controls={true} />
        </form>

    )
}

export default UploadForm;
